import React, { useState } from 'react';
import styled from 'styled-components';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import Button from '../Button/Button';

const AcornForm = ({ setResult }) => {
    const [inputState, setInputState] = useState({
        amount: '',
        multiplier: '',
        roundOffValue: '',
    });

    const { amount, multiplier, roundOffValue } = inputState;

    const handleInput = name => e => {
        setInputState({...inputState, [name]: e.target.value});
    }

    const calculateAcorns = () => {
        const amountFloat = parseFloat(amount);
        const multiplierFloat = parseFloat(multiplier);
        const roundOffValueFloat = parseFloat(roundOffValue);

        if (!isNaN(amountFloat) && !isNaN(multiplierFloat) && !isNaN(roundOffValueFloat)) {
            const totalAcorns = Math.round((amountFloat * multiplierFloat) / roundOffValueFloat);
            setResult(totalAcorns);
        } else {
            setResult(null);
        }
    };

    return (
        <ExpenseFormStyled>
            <div className="input-control">
                <input
                    type="number"
                    value={amount}
                    onChange={handleInput('amount')}
                    placeholder="Amount"
                />
            </div>
            <div className="input-control">
                <input
                    type="number"
                    value={multiplier}
                    onChange={handleInput('multiplier')}
                    placeholder="Multiplier"
                />
            </div>
            <div className="input-control">
                <input
                    type="number"
                    value={roundOffValue}
                    onChange={handleInput('roundOffValue')}
                    placeholder="Round-off Value"
                />
            </div>
            <div className="submit-btn">
                <Button
                    name="Calculate"
                    onClick={calculateAcorns}
                />
            </div>
        </ExpenseFormStyled>
    );
};

const ExpenseFormStyled = styled.form`
display: flex;
flex-direction: column;
gap: 2rem;
input, textarea, select{
    font-family: inherit;
    font-size: inherit;
    outline: none;
    border: none;
    padding: .5rem 1rem;
    border-radius: 5px;
    border: 2px solid #fff;
    background: transparent;
    resize: none;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    color: rgba(34, 34, 96, 0.9);
    &::placeholder{
        color: rgba(34, 34, 96, 0.4);
    }
}
.input-control{
    input{
        width: 100%;
    }
}

.selects{
    display: flex;
    justify-content: flex-end;
    select{
        color: rgba(34, 34, 96, 0.4);
        &:focus, &:active{
            color: rgba(34, 34, 96, 1);
        }
    }
}

.submit-btn{
    button{
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        &:hover{
            background: var(--color-green) !important;
        }
    }
}
`;

export default AcornForm;
